#include<windows.h>#include<iostream>#include<math.h>#include <stdio.h>#include<GL/gl.h>#include <GL/glut.h>using namespace std;void Circle(void){    glClear(GL_COLOR_BUFFER_BIT);    glBegin(GL_POINTS);    int x = 0;    int y = 0;    int r = 10;	int triangleAmount = 1000;	float PI = 3.1416;	glBegin(GL_TRIANGLE_FAN);		glVertex2f(x, y);		for(int i = 0; i <= triangleAmount;i++) {			glVertex2f(                x + (r * cos(i *  2 * PI / triangleAmount)),			    y + (r * sin(i * 2 * PI / triangleAmount))			);		}    glEnd();    glFlush();}void myInit(){    glClearColor(1.0, 1.0, 1.0, 0.0);    glColor3f(0.0f, 0.0f, 0.0f);    glPointSize(4.0);    glMatrixMode(GL_PROJECTION);    glLoadIdentity();    gluOrtho2D(-20.0, 20.0,-20.0, 20.0);}int main(int argc, char** argv){    glutInit(&argc, argv);    glutInitDisplayMode (GLUT_SINGLE | GLUT_RGB);    glutInitWindowSize (640, 640);    glutInitWindowPosition (100, 150);    glutCreateWindow ("Circle using Triangle Fan");    glutDisplayFunc(Circle);    myInit();    glutMainLoop();}